import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [file1, setFile1] = useState(null);
  const [file2, setFile2] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!file1 || !file2) return alert("Please upload both files");

    const formData = new FormData();
    formData.append('file1', file1);
    formData.append('file2', file2);

    try {
      const response = await axios.post('http://localhost:5000/upload', formData, {
        responseType: 'blob',
      });

      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', 'matched_output.xlsx');
      document.body.appendChild(link);
      link.click();
    } catch (err) {
      alert('Error processing files');
    }
  };

  const containerStyle = {
    maxWidth: '500px',
    margin: '50px auto',
    padding: '30px',
    borderRadius: '12px',
    background: '#f9f9f9',
    boxShadow: '0 4px 20px rgba(0, 0, 0, 0.4)',
    fontFamily: 'Arial, sans-serif',
  };

  const headingStyle = {
    textAlign: 'center',
    marginBottom: '20px',
    color: '#333',
  };

  const inputGroup = {
    marginBottom: '15px',
    display: 'flex',
    flexDirection: 'column',
  };

  const labelStyle = {
    marginBottom: '8px',
    fontWeight: 'bold',
  };

  const inputStyle = {
    padding: '8px',
    border: '1px solid #ccc',
    borderRadius: '6px',
  };

  const buttonStyle = {
    marginTop: '10px',
    padding: '10px 15px',
    backgroundColor: '#007bff',
    color: 'white',
    border: 'none',
    borderRadius: '6px',
    cursor: 'pointer',
    fontWeight: 'bold',
  };

  return (
    <div style={containerStyle}>
      <h2 style={headingStyle}>Srifin Excel Tool to Merge Files</h2>
      <hr style={mar} />
      <form onSubmit={handleSubmit}>
        <div style={inputGroup}>
          <label style={labelStyle}>LMS File:</label>
          <input type="file" onChange={e => setFile1(e.target.files[0])} style={inputStyle} />
        </div>
        <div style={inputGroup}>
          <label style={labelStyle}>Zing-HR File:</label>
          <input type="file" onChange={e => setFile2(e.target.files[0])} style={inputStyle} />
        </div>
        <button type="submit" style={buttonStyle}>Merge and Download</button>
      </form>
    </div>
  );
}

export default App;
